#ifndef BASE_HPP
#define BASE_HPP

#include "../inc/common.hpp"

#include "Entity.hpp"
#include "BasicShape.hpp"
#include "BasicUtils.hpp"


class Base {
private:
    // Saves the amount of time generated by the timeUtil.
    BasicUtils ut;
    float totalTime;

    sf::RenderWindow window;
    sf::View view;

    Entity player;
    std::vector<sf::FloatRect> playerBorders;
    sf::Vector2f playerMoveAmount;

    int currentLevel;

    std::vector<BasicShape> collides;
    std::vector<BasicShape> rooms;

public:

    // Init class.
    Base();

    // Init functions.
    void createWindow();
    void createPlayer();
    void designLevel(int/*, int*/); // 1 = Rooms

    // Required by designLevel().
    void convertCoords(std::string, sf::Vector2f&);

    // Main Functions.
    bool gameLoop();
    bool checkControls();

    // Required by checkControls().
    float calculateFrameStep();
    void checkMoves();

    // Required by checkMoves().
    void updatePlayerBorders();

    void drawWindow();
};

#endif
